import React from 'react';
import { Link } from 'react-router-dom';
import InputControl from '../inputControl/InputControl';
import styles from "./Signup.module.css"

const Signup = () => {

    const [user, setUser] = useState({
        name : "",
        email : "",
        password: "",
      })
    
      let name, value;
      const getData = (e)=>{
        name = e.target.name;
        value = e.target.value;
    
        setUser({...user, [name]: value});
      }
    
    //   const[errorMsg, setErrorMsg] = useState("");
    
      const[submitButtonDisabled,setsubmitButtonDisabled ] = useState(false);
    
      const postData= async(e)=>{
        e.preventDefault();
    
        const{name,email,password}=user;
    
        if(!name||!email||!password ){
            alert("Fill all fields");
            // setErrorMsg("Fill all fields")
        }
        else{
            createUserWithEmailAndPassword(auth,email,password).then(async(res)=>{
                setsubmitButtonDisabled(false);
    
                const people = res.people;
    
                await updateProfile(people, {
                    displayName: user.name,
                })
                // console.log(res);
    
                navigate('/login');
    
               
    
            })
    
            .catch((e)=>{
                setsubmitButtonDisabled(false);
                console.log(e);
            });
        }
    }    
  return (
    <div className={styles.container}>
        <div className={styles.innerBox}>
            <h1 className={styles.heading}>signup</h1>
            <InputControl label="Name" name="name" type = "text" placeholder ="Enter Your Name" onChange={getData} />

            <InputControl label="Email" name="email" type ="email"  placeholder ="Enter Your Email" onChange={getData} />

            <InputControl label="Password" type = "password" name="password" placeholder ="Enter Your Password" onChange={getData} />

            <div className={styles.footer}>

                <button type='submit'>signup</button>

                <p>Dont have account?</p> <span><Link to="/login">Login</Link></span>
            </div>

        </div>
    </div>

  )
}

export default Signup
