import React from 'react';
import { Link ,  useNavigate} from 'react-router-dom';
import InputControl from '../inputControl/InputControl';
import styles from "./Login.module.css"
import { useState } from 'react';
import {createUserWithEmailAndPassword, updateProfile} from "firebase/auth";
import {auth} from "../../firebase";

const Login = () => {
    const navigate = useNavigate();

    const [user, setUser] = useState({
        email : "",
        password: "",
      })
    
      let name, value;
      const getData = (e)=>{
        name = e.target.name;
        value = e.target.value;
    
        setUser({...user, [name]: value});
      }
    
      const[errorMsg, setErrorMsg] = useState("");
    
      const[submitButtonDisabled,setsubmitButtonDisabled ] = useState(false);
    
      const postData= async(e)=>{
        e.preventDefault();
    
        const{name,email,password}=user;
    
        if(!email||!password ){
            // alert("Fill all fields");
            setErrorMsg("Fill all fields");
            return;
        }
        else{
            setErrorMsg("")
            createUserWithEmailAndPassword(auth,email,password).then(async(res)=>{
                setsubmitButtonDisabled(true);
    
                const people = res.people;
    
                await updateProfile(people, {
                    displayName: name,
                })
                // console.log(res);
    
                navigate('/');
    
               
    
            })
    
            .catch((e)=>{
                setsubmitButtonDisabled(false);
                setErrorMsg(e.message)
                // console.log(e);
            });
        }
    }    
  return (
    <div className={styles.container}>
        <div className={styles.innerBox}>
            <h1 className={styles.heading}>Login</h1>
            <InputControl label="Email" type ="email"  placeholder ="Enter Your Email" />
            <InputControl label="Password" type = "password" placeholder ="Enter Your Password" />

            <div className={styles.footer}>
                <b className={styles.errror}>{errorMsg}</b>
                <button onClick={postData}>Login</button>
                <p>Dont have account?</p> <span><Link to="/signup">Sign up</Link></span>
            </div>

        </div>
    </div>

  )
}

export default Login
